	// Fonction pour générer l'URL de vérification
  import { SITE_CONFIG } from "@/config/site";
  export function buildVerificationUrl(serial: string): string {
    // Génère une URL complète pour la vérification du certificat
    return `${SITE_CONFIG.url}/verify/${encodeURIComponent(serial)}`;
  }
